export default function responseConstructor (esp3Packet, typeDesc = {}) {
  const RETURN_CODE_LOCATION = 0
  const responseTypes = [
    { number: 0, name: 'RET_OK' },
    { number: 1, name: 'RET_ERROR' },
    { number: 2, name: 'RET_NOT_SUPPORTED' },
    { number: 3, name: 'RET_WRONG_PARAM' },
    { number: 4, name: 'RET_OPERATION_DENIED' },
    { number: 5, name: 'RET_LOCK_SET' },
    { number: 6, name: 'RET_BUFFER_TO_SMALL' },
    { number: 7, name: 'RET_NO_FREE_BUFFER' }
  ]
  const packet = esp3Packet
  var res = Object.create({})
  if (typeDesc[packet.data[RETURN_CODE_LOCATION]]) {
    typeDesc[packet.data[RETURN_CODE_LOCATION]].forEach(field => {
      if (field.location) {
        res[field.name] = field.retFunc(packet[field.location].slice(field.offset, field.offset + field.length))
      } else {
        res[field.name] = field.value
      }
    })
  } else {
    res.returnValue = packet.data[RETURN_CODE_LOCATION]
    res.returnCode = responseTypes[res.returnValue]
  }
  return Object.freeze(res)
}
