#!/usr/bin/env node
const SerialportSender = require('@enocean-js/serialport-sender').SerialportSender
const ESP3Parser = require('@enocean-js/serialport-parser').ESP3Parser
const SerialPort = require('serialport')
const fs = require('fs')
var http_port = 4242
var serial_port = '/dev/ttyUSB0'

for (var i = 2; i < process.argv.length; i++) {
  switch (process.argv[i]) {
    case '-p':
    case '--port':
      var next = process.argv[i + 1]
      if (!isNaN(next)) {
        http_port = next
      } else {
        throw new Error('port must be a number')
      }
      break
    case '-s':
    case '--serialport':
      // TODO: sanity checks
      serial_port = process.argv[i + 1]
      break
  }
}

const port = new SerialPort(serial_port, { baudRate: 57600 })
port.on('open', () => { console.log(`${serial_port} open`) })
port.on('error', () => { port.close(); throw new Error(`could not open ${serial_port}`) })
const parser = new ESP3Parser({ maxBufferSize: 2000 })
port.pipe(parser)
var sender = SerialportSender({ port: port, parser: new ESP3Parser({ maxBufferSize: 2000 }) })
var app = require('express')()
var http = require('http').Server(app)
var io = require('socket.io')(http)

const rateLimit = require('express-rate-limit')
const limiter = rateLimit({
  windowMs: 60 * 1000, // 15 minutes
  max: 200
})
app.use(limiter)

app.get('/', function (req, res) {
  fs.readFile(`${__dirname}/index.html`, (err, data) => {
    res.send(data.toString().replace('@@PORT@@', http_port))
  })
})

io.on('connection', function (socket) {
  socket.on('send', async function (tel) {
    try {
      var res = await sender.send(tel)
      socket.emit('response', res.getRawPacket())
    } catch (err) {
      console.log('err')
      socket.emit('error', err)
    }
  })
})

http.listen(http_port, function () {
  console.log('listening on *:' + http_port)
})

setInterval(() => { }, 10)
parser.on('data', data => { io.emit('data', data.toString('hex')) })
